--IMG = %APPDATA%/ExampleProgIcon.png--AUTHOR = Jakob Riedle (DuffsDevice)--VERSION = 1.0--DESC = Application to demonstrate some featuresthe possibilities that WinDS offers--COPYRIGHT = (C) 2014--LANG = neutralusing "System.Event"using "System.ImageFile"using "System.Timer"using "Drawing.Bitmap"using "Drawing.Color"using "UI.Gadget"using "UI.Calendar"using "UI.Counter"using "UI.ColorPicker"using "UI.ImageGadget"using "UI.Button"using "UI.Select"using "UI.CheckBox"using "UI.Label"using "UI.Radio"using "UI.RadioGroup"using "UI.ScrollArea"using "UI.ProgressBar"using "UI.TextBox"using "UI.TextArea"using "UI.Window"local wnd1local wnd2local animCanvaslocal cntrlocal clrpckrlocal clndrlocal imggadgetlocal btnlocal slclocal chkbxlocal lbllocal lbl1local lbl2local lbl3local radgrplocal rad1local rad2local rad3local scrllarealocal prglocal txtbxlocal txtarealocal timer1local imgFilelocal timer1function main()	-- Radio box test	wnd1 = Window( 5 , 5 , 80 , 47 , "Radiobuttons" , "notResizeable" )		radgrp = RadioGroup()	rad1 = Radio( 3 , 3 , radgrp )	rad2 = Radio( 3 , 13 , radgrp )	rad3 = Radio( 3 , 23 , radgrp )	lbl1 = Label( 15 , 4 , nil , nil , "Option 1" )	lbl2 = Label( 15 , 14 , nil , nil , "Option 2" )	lbl3 = Label( 15 , 24 , nil , nil , "Option ...3!" )		wnd1.addChild( rad1 )	wnd1.addChild( rad2 )	wnd1.addChild( rad3 )		wnd1.addChild( lbl1 )	wnd1.addChild( lbl2 )	wnd1.addChild( lbl3 )		System.addChild( wnd1 )	--		-- Colorpicker, calendar Test	wnd2 = Window( 5 , 5 , 168 , 100 , "Special Gadgets" , "notResizeable" )	local e = Event()	wnd2.setInternalEventHandler( "onClose" , System.exit )		clrpckr = ColorPicker( 3 , 3 , 60 , 40 )	clrpckr.setInternalEventHandler( "onEdit" , colorHandler )	clndr = Calendar( 66 , 3 , 99 , 80 , 2013 , 6 , 5 )		wnd2.addChild( clrpckr )	wnd2.addChild( clndr )	--		System.addChild( wnd2 )		-- Animation Test	imgFile = ImageFile( "neu.gif" , true )		animCanvas = ImageGadget( 0 , 0 , imgFile.readBitmap() )		timer1 = Timer( timerCallback , 100 , true )		System.addChild( animCanvas )		timer1.start()	--endlocal animationStep = 0function timerCallback()	animationStep = animationStep+1	if animationStep == imgFile.getNumPages() then		animationStep = 0	end	animCanvas.image = imgFile.readBitmap( animationStep )endfunction colorHandler( event )	local bmp = Bitmap( 6 , 6 )	local col = clrpckr.color	print( col )	if col.lum < 50 then		bmp.reset( Color.fromRGB( 31 , 31 , 31 ) )	else		bmp.reset( Color.fromRGB( 0 , 0 , 0 ) )	end	bmp.drawFilledRect( 1 , 1 , 4 , 4 , clrpckr.color )		wnd2.icon = bmpend