--FILE_ICON	= %APPDATA%/ExampleProgIcon.png--AUTHOR	= WinDS--VERSION	= 1.0--DESC		= Application to demonstrate some featuresthe possibilities that WinDS offers--COPYRIGHT	= (C) 2014--FILE_NAME	= ExampleProg--NAME		= Example Programusing "System.Event"using "System.ImageFile"using "System.Timer"using "System.Menu"using "Drawing.Bitmap"using "Drawing.Color"using "UI.Gadget"using "UI.Calendar"using "UI.Counter"using "UI.GradientColorPicker"using "UI.ImageGadget"using "UI.Button"using "UI.Select"using "UI.CheckBox"using "UI.Label"using "UI.Radio"using "UI.RadioGroup"using "UI.ScrollArea"using "UI.ProgressBar"using "UI.TextBox"using "UI.TextArea"using "UI.MainFrame"using "UI.Window"using "UI.ContextMenu"using "UI.Popup"local wnd1local wnd2local cntrlocal ctmlocal clrpckrlocal clndrlocal imggadgetlocal btnlocal slclocal chkbxlocal lbllocal lbl1local lbl2local lbl3local radgrplocal rad1local rad2local rad3local scrllarealocal prglocal txtbxlocal txtareafunction dump(o,id)	id = id or ''	if type(o) == 'table' then		local s = '{ '		for k,v in pairs(o) do			if type(k) ~= 'number' then				k = '"'..k..'"'			end			s = s .. '\n' .. id .. ' ['..k..'] = ' .. dump(v,id..' ') .. ','		end		return s .. '\n' .. id .. '} '	else		return tostring(o)	endendfunction main()	-- Radio box test	wnd1 = Window( 10, 40 , 80 , 47 , "Click right here" , false , false , "notResizeable | rightClickable" )		radgrp = RadioGroup()	rad1 = Radio( 3 , 3 , radgrp )	rad2 = Radio( 3 , 13 , radgrp )	rad3 = Radio( 3 , 23 , radgrp )	lbl1 = Label( 15 , 4 , nil , nil , "Option 1" )	lbl2 = Label( 15 , 14 , nil , nil , "Option 2" )	lbl3 = Label( 15 , 24 , nil , nil , "Option ...3!" )		ctm = ContextMenu( nil , { { 1 , "1st Entry" } , { 2 , "Another Entry" } } )		--print( dump( ctm.currentList ) )		wnd1.addChild( rad1 )	wnd1.addChild( rad2 )	wnd1.addChild( rad3 )		wnd1.addChild( lbl1 )	wnd1.addChild( lbl2 )	wnd1.addChild( lbl3 )		wnd1.setUserEventHandler( "onMouseRightClick" , rightClickHandler )	--		-- Colorpicker, calendar Test	wnd2 = System.getMainFrame( 168 , 100 , "Special Gadgets" , "notResizeable" )	local e = Event()		clrpckr = GradientColorPicker( 3 , 3 , 60 , 40 )	clrpckr.setInternalEventHandler( "onEdit" , colorHandler )	clndr = Calendar( 66 , 3 , 99 , 80 , 2013 , 6 , 5 )		wnd2.addChild( clrpckr )	wnd2.addChild( clndr )		-- Also append wnd1 to the host	wnd1.parent = wnd2.parentendfunction colorHandler( event )	local bmp = Bitmap( 6 , 6 )	local col = clrpckr.color		if col.lum < 50 then		bmp.reset( Color.fromRGB( 31 , 31 , 31 ) )	else		bmp.reset( Color.fromRGB( 0 , 0 , 0 ) )	end	bmp.drawFilledRect( 1 , 1 , 4 , 4 , col )		wnd2.icon = bmpendfunction rightClickHandler( event )	local pos = event.gadget.getAbsolutePosition()	ctm.show( pos.first + 2 , pos.second + 2 )end